\begin{equation}
\label{eq:C_G_JSD}
    \begin{aligned}
    C(G) =& -log(4) + KL(p_s\parallel \frac{p_s+p_m}{2}) + KL(p_m\parallel \frac{p_s+p_m}{2}) \\
    & -log(4) + KL(p_t\parallel \frac{p_t+p_m}{2}) + KL(p_m\parallel \frac{p_t+p_m}{2}) \\
    =& -2log(4) + 2JSD(p_s \parallel p_m) + 2JSD(p_t \parallel p_m)
    \end{aligned}
\end{equation}

