\begin{minipage}{0.5\textwidth}
\footnotesize
\begin{equation} \label{eq:projection_model}
\lambda \begin{bmatrix*}[c] u \\ v \\ 1 \end{bmatrix*} = \begin{bmatrix*} \textbf{K}  & \mathbf{O}_3 \end{bmatrix*}
    \begin{bmatrix*}[c] 
    \mathbf{R} & \mathbf{C} \\ \mathbf{O}_3^T & 1 
    \end{bmatrix*}
    \begin{bmatrix*}[c]
    X_W \\ Y_W \\ Z_W \\ 1 
    \end{bmatrix*}
\end{equation}
\begin{equation}
\mathbf{K}=
\begin{bmatrix*}[c]
k_{u}f     & 0    & k_{u}x_{0} \\
0   & -k_{v}f  & k_{v}y_{0} \\
0   & 0    & 1 
\end{bmatrix*}
\label{eqn:K_params}
\end{equation}
